mapper.insert:
INSERT INTO employee(
	employee_id
	, employee_pw
	, employee_name
) VALUES (
	#{employeeId} 
	, PASSWORD(#{employeePw})
	, #{employeeName}
)

mapper.select:
SELECT employee_no employeeNo , employee_id employeeId , employee_name employeeName FROM employee
employee_name LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY employee_no DESC LIMIT #{beginRow}, #{rowPerPage}
